@bot.message_handler(commands=['soveti'])
def ask_for_proda(message):
    user_states[message.chat.id] = 'waiting_for_response'  # –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ
    bot.reply_to(message, "–ú—ã –ø—Ä–∏–≥–æ—Ç–æ–≤–∏–ª–∏ –Ω–µ–∫–æ—Ç–æ—Ä—ã–µ —Å–æ–≤–µ—Ç—ã –ø–æ –ø–∏—Ç–∞–Ω–∏—é –¥–ª—è –≤—Å–µ—Ö –Ω–∞—à–∏—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π.")
    bot.send_message(message.chat.id, "–î–ª—è –Ω–∞—á–∞–ª–∞ –¥–∞–≤–∞–π—Ç–µ —É—Ç–æ—á–Ω–∏–º: \n 1-–Ø –Ω–µ –∏—Å–∫–ª—é—á–∞—é –∏–∑ —Å–≤–æ–µ–≥–æ —Ä–∞—Ü–∏–æ–Ω–∞ –Ω–∏–∫–∞–∫–æ–π —Ç–∏–ø –ø—Ä–æ–¥—É–∫—Ç–æ–≤\n 2-–Ø –∏—Å–∫–ª—é—á–∞—é –∏–∑ —Å–≤–æ–µ–≥–æ —Ä–∞—Ü–∏–æ–Ω–∞ –º—è—Å–æüçñ‚ùå\n 3-–Ø –∏—Å–∫–ª—é—á–∞—é –∏–∑ —Å–≤–æ–µ–≥–æ —Ä–∞—Ü–∏–æ–Ω–∞ –º–æ–ª–æ—á–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ãü•õ‚ùå\n 4-–Ø –∏—Å–∫–ª—é—á–∞—é –∏–∑ —Å–≤–æ–µ–≥–æ —Ä–∞—Ü–∏–æ–Ω–∞ –º—è—Å–Ω—ã–µ –∏ –º–æ–ª–æ—á–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã üçñü•õ‚ùå\n\n –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –Ω–æ–º–µ—Ä –≤–∞—à–µ–≥–æ –≤—ã–±–æ—Ä–∞:")

@bot.message_handler(func=lambda message: user_states.get(message.chat.id) == 'waiting_for_response')
def handle_user_response(message):
    if message.text.lower() == "1":
        bot.send_message(message.chat.id, "–î–ª—è –ª—é–¥–µ–π, –Ω–µ –∏—Å–∫–ª—é—á–∞—é—â–∏—Ö –Ω–∏—á–µ–≥–æ –∏–∑ —Å–≤–æ–µ–≥–æ —Ä–∞—Ü–∏–æ–Ω–∞, –ø—Ä–µ–¥–ª–∞–≥–∞–µ–º –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è —Å –ü—Ä–æ–≤–µ—Ä–µ–Ω–Ω—ã–º–∏ –∏—Å—Ç–æ—á–Ω–∏–∫–∞–º–∏.\n 1)–†–æ–ª–∏–∫ —Å –∫–æ—Ä–æ—Ç–∫–∏–º–∏ —Å–æ–≤–µ—Ç–∞–º–∏ –ø–æ –ø—Ä–∞–≤–∏–ª—å–Ω–æ–º—É –ø–∏—Ç–∞–Ω–∏—é https://yandex.ru/video/preview/17504274598371715017 \n 2)–ü—Ä–∏–Ω—Ü–∏–ø—ã –∑–¥–æ—Ä–æ–≤–æ–≥–æ –ø–∏—Ç–∞–Ω–∏—è –æ—Ç –†–û–°–ü–û–¢–†–ï–ë–ù–ê–î–ó–û–†–ê https://14.rospotrebnadzor.ru/content/2090/79455/ \n 3) 1000 —Ä–µ—Ü–µ–ø—Ç–æ–≤ –¥–æ–º–∞—à–Ω–∏—Ö —Ä–µ—Ü–µ–ø—Ç–æ–≤ –∑–¥–æ—Ä–æ–≤–æ–≥–æ –ø–∏—Ç–∞–Ω–∏—è https://1000.menu/catalog/sbalansirovannoe-pitanie")
    elif message.text.lower() == "2":
        bot.send_message(message.chat.id, "–î–ª—è –ª—é–¥–µ–π, –∏—Å–∫–ª—é—á–∞—é—â–∏—Ö –º—è—Å–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã, –ø—Ä–µ–¥–ª–∞–≥–∞–µ–º –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è —Å –ø—Ä–æ–≤–µ—Ä–µ–Ω–Ω—ã–º–∏ –∏—Å—Ç–æ—á–Ω–∏–∫–∞–º–∏.\n 1)–†–æ–ª–∏–∫ —Å —Å–æ–≤–µ—Ç–∞–º–∏ –ø–æ –ø–∏—Ç–∞–Ω–∏—é –±–µ–∑ –º—è—Å–∞ https://youtu.be/KPO0A90XqzE \n 2)–ß–µ–º –∑–∞–º–µ–Ω–∏—Ç—å –º—è—Å–æ https://rutube.ru/video/8a225add9e7d4800501e4d73e2a86756/?r=plwd \n 3)–ü–ü –±–ª—é–¥–∞ –±–µ–∑ –º—è—Å–∞ https://1000.menu/meals/9022-10427")
    elif message.text.lower() == "3":
        bot.send_message(message.chat.id, "–î–ª—è –ª—é–¥–µ–π, –∏—Å–∫–ª—é—á–∞—é—â–∏—Ö –º–æ–ª–æ—á–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã, –ø—Ä–µ–¥–ª–∞–≥–∞–µ–º –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è —Å –ø—Ä–æ–≤–µ—Ä–µ–Ω–Ω—ã–º–∏ –∏—Å—Ç–æ—á–Ω–∏–∫–∞–º–∏.\n 1)–†–æ–ª–∏–∫ –æ —Ç–æ–º —á–µ–º –∑–∞–º–µ–Ω–∏—Ç—å –º–æ–ª–æ—á–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã https://yandex.ru/video/preview/13100322906635975298 \n 2) –†–µ—Ü–µ–ø—Ç—ã –±–µ–∑ –º–æ–ª–æ—á–Ω—ã—Ö –ø—Ä–æ–¥—É–∫—Ç–æ–≤ https://www.edimdoma.ru/retsepty/tags/9056-pp-retsepty-bez-molochnyh-produktov \n 3)–†–∞—Ü–∏–æ–Ω –ø–∏—Ç–∞–Ω–∏—è –±–µ–∑ –º–æ–ª–æ–ª—á–Ω—ã—Ö –ø—Ä–æ–¥—É–∫—Ç–æ–≤ https://dgp6-omsk.ru/ru/racion-pitanija-bez-molochnyh-produktov")
    elif message.text.lower() == "4":
        bot.send_message(message.chat.id,"–î–ª—è –ª—é–¥–µ–π, –∏—Å–∫–ª—é—á–∞—é—â–∏—Ö –º—è—Å–Ω—ã–µ –∏ –º–æ–ª–æ—á–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã, –ø—Ä–µ–¥–ª–∞–≥–∞–µ–º –æ–∑–Ω–∞–∫–æ–º–∏—Ç—å—Å—è —Å –ø—Ä–æ–≤–µ—Ä–µ–Ω–Ω—ã–º–∏ –∏—Å—Ç–æ—á–Ω–∏–∫–∞–º–∏.\n 1) –†–æ–ª–∏–∫ –æ –ø–æ–ª–Ω–æ—Ü–µ–Ω–Ω–æ–º –º–µ–Ω—é –Ω–∞ –Ω–µ–¥–µ–ª—é https://yandex.ru/video/preview/3342349212361481234 \n 2) –ö–∞–∫ —Å–±–∞–ª–∞–Ω—Å–∏—Ä–æ—Ä–≤–∞—Ç—å —Ä–∞—Ü–∏–æ–Ω https://77.rospotrebnadzor.ru/index.php/press-centr/186-press-centr/12308-vegetarianstvo-kak-sbalansirovat-ratsion-01-11-2023 \n 3) –†–µ—Ü–µ–ø—Ç—ã, –∏—Å–∫–∞–ª—é—á–∞—é—â–∏–µ –º—è—Å–Ω—ã–µ –∏ –º–æ–ª–æ—á–Ω—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã https://1000.menu/meals/13-4717")
    user_states[message.chat.id] = None  # –°–±—Ä–∞—Å—ã–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø–æ—Å–ª–µ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –æ—Ç–≤–µ—Ç–∞
